function ID(spec) {
	var id = spec.id;
	var name = spec.name;
	var displayName = spec.displayName;

	if (id == null) {id = name};
	if (id == null) {id = displayName};

	if (name == null) {name = id};
	if (name == null) {name = displayName};

	if (displayName == null) {displayName = id};
	if (displayName == null) {displayName = name};

	return {
		id: id,
		name: name,
		displayName: displayName
	}
}



exports.ID = ID;
